import React, { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Register from "./Register";
import { useNavigate } from "react-router-dom";
import UserLogin from "./UserLogin";
import Alert from "./Alert";
import "./style.css";

export default function Userportal(props) {
  const [alert, setAlert] = useState(null);
  const [storedUsers, setStoredUsers] = useState([]); // State to store registered users
  
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    username: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const navigate = useNavigate();

  const storeUserData = (userData) => {
    if(userData.email===props.data.email){setStoredUsers([...storedUsers, userData]);}
    else{
      setStoredUsers(storedUsers)
    }
    
    return true; 
  };

  const handleSubmit = (e) => {
    e.preventDefault();
const isStored = storeUserData(formData);
    if (formData.email === props.data.email && isStored) {
      navigate("/Student");
      console.log(storedUsers)
      
    } else {

         navigate("/Userportal/Register");
        setAlert({ mssg: "Enter The Institute Mail ID", Res: "danger" });
        setFormData({ email: "", password: "", username: "" });
        setTimeout(() => {
          setAlert(null);
        }, 1800);
     
    }
  };

  return (
    <div className="body">
      <Alert alert={alert} />
      <Routes>
        <Route path="/" element={<Navigate to="Login" />} />
        <Route path="Login" element={<UserLogin />} />
        <Route
          path="Register"
          element={
            <Register
              formData={formData}
              handleSubmit={handleSubmit}
              handleChange={handleChange}
            />
          }
        />
      </Routes>
    </div>
  );
}
