import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Alert from './Alert';


export default function Register(props) {
  const [formData, setFormData] = useState({ email: '', password: '' ,username:''});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add registration logic (e.g., API call)
    console.log('Register:', formData);
    if(formData.email===props.data.email){navigate('/Student');}
    else{
      navigate('/Userportal/Register');
    }
    
  };

  return (
    <div className="register active">
      
      <div className="right">
        <h1>Register Here</h1>
        <form onSubmit={handleSubmit}>
          <p>
           <i className="fa-solid fa-envelope"></i>
            <input
              type="email"
              name="email"
              placeholder="Please Enter your mail Id"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </p>
          <p>
            <i className="fa-solid fa-circle-user"> </i>
            <input
              type="username"
              name="username"
              placeholder="Please Enter your Username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </p>
          <p>
         <i class="fa-solid fa-lock"></i>
            <input
              type="password"
              name="password"
              placeholder="Please Enter your Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </p>
          <button type="submit" >Submit</button>
          <p>
            Have an account?
            <Link to="/Userportal/Login" className="reg">
              Sign In
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}
