import React from 'react'
import "./Enroll.css"

export default function Enroll(props) {
  
    const [formData, setFormData] = React.useState({
        fullname: '',
        email: '',
        course: '',
      });
      const [status, setStatus] = React.useState('');

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.name && formData.email && formData.course) {
          setStatus('Enrollment submitted successfully!');
          setFormData({ name: '', email: '', course: '' });
        } else {
          setStatus('Please fill in all fields.');
        }
      };

  return (
    <div>
        <main
        className="main-content "
        style={{
          marginLeft: props.isExpanded
            ? window.innerWidth < 768
              ? "3rem"
              : "11.5rem"
            : window.innerWidth > 768
            ? "3rem"
            : "3rem",
        }}
>
        <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 sm:p-6 lg:p-8">
          <div className="bg-white p-6 sm:p-8 rounded-lg shadow-lg w-full max-w-xs sm:max-w-md lg:max-w-lg">
            <h1 className="text-xl sm:text-2xl lg:text-3xl font-bold mb-6 text-center">Course Enrollment</h1>
            <div className="space-y-4">
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="mt-1 block w-full p-2 sm:p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm sm:text-base"
                  placeholder="Enter your name"
                />
              </div>
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="mt-1 block w-full p-2 sm:p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm sm:text-base"
                  placeholder="Enter your email"
                />
              </div>
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">Select Course</label>
               <input class="form-check-input" type="checkbox" id="checkboxNoLabel" value="" aria-label="...">jjjj</input>
              </div>
              <button
                onClick={handleSubmit}
                className="w-full bg-blue-600 text-white p-2 sm:p-3 rounded-md hover:bg-blue-700 transition text-sm sm:text-base"
              >
                Enroll Now
              </button>
              {status && (
                <p className={`text-center text-sm sm:text-base ${status.includes('success') ? 'text-green-600' : 'text-red-600'}`}>
                  {status}
                </p>
              )}
            </div>
          </div>
        </div></main>
      
    </div>
  )
}
