import React, { useRef, useEffect, useState } from 'react';
import "./Timetable.css"


const Timetable= () => {
  const sliderRef = useRef(null);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true);

  // Sample data
  const tableData = [
    { id: 1, name: 'John Doe', age: 30, city: 'New York', occupation: 'Engineer', department: 'IT', joinDate: '2023-01-15' },
    { id: 2, name: 'Jane Smith', age: 28, city: 'Los Angeles', occupation: 'Designer', department: 'Creative', joinDate: '2022-06-20' },
    { id: 3, name: 'Mike Johnson', age: 35, city: 'Chicago', occupation: 'Manager', department: 'Operations', joinDate: '2021-03-10' },
    { id: 4, name: 'Emily Davis', age: 27, city: 'Houston', occupation: 'Analyst', department: 'Finance', joinDate: '2023-09-05' },
    { id: 5, name: 'Alex Brown', age: 32, city: 'Seattle', occupation: 'Developer', department: 'IT', joinDate: '2022-11-12' },
  ];

  
  return (
    <div className="table-slider-container">
      <div className="slider-container" ref={sliderRef}>
        <table className="data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Ag</th>
              <th>City</th>
              <th>Occupation</th>
              <th>Department</th>
              <th>Join Date</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((row) => (
              <tr key={row.id}>
                <td>{row.id}</td>
                <td>{row.name}</td>
                <td>{row.age}</td>
                <td>{row.city}</td>
                <td>{row.occupation}</td>
                <td>{row.department}</td>
                <td>{row.joinDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      
    </div>
  );
};

export default Timetable;
