import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Left from './components/Left';
import Right from './components/Right';
import Dashboard from './components/Dashboard';
import Profile from './components/Profile';
import Enroll from './components/Enroll';
import Timetable from './components/Timetable';
import Polls from './components/Polls';

function Student() {
  const [isExpanded, setIsExpanded] = useState(true);

  const hide = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="app-container"> {/* Add a container for layout */}
      <Header />
      <div className="main-content">
        <aside className="sidebar">
          <Left isExpanded={isExpanded} setIsExpanded={setIsExpanded} hide={hide} />
        </aside>
        <div className="content">
          <Right />
          <Routes>
            <Route path="/" element={<Dashboard isExpanded={isExpanded} setIsExpanded={setIsExpanded} />} />
            <Route path="Profile" element={<Profile isExpanded={isExpanded} setIsExpanded={setIsExpanded} />} />
            <Route path="Enroll" element={<Enroll isExpanded={isExpanded} setIsExpanded={setIsExpanded} />} />
            <Route path="Timetable" element={<Timetable isExpanded={isExpanded} setIsExpanded={setIsExpanded} />} />
            <Route path="Polls" element={<Polls isExpanded={isExpanded} setIsExpanded={setIsExpanded} />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default Student;
