import React , { useState } from "react";
import { Routes, Route, Navigate } from 'react-router-dom';
import Register from './Register';
import {  useNavigate } from 'react-router-dom';
import UserLogin from './UserLogin';
import Alert from './Alert'
import './style.css';

export default function Userportal(props) {
  const [alert,setAlert]=useState(null)
   const [formData, setFormData] = useState({ email: '', password: '' ,username:''});
   const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };


    const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    

    if(formData.email===props.data.email){navigate('/Student');}
    else{
      navigate('/Userportal/Register');
      setAlert({mssg:'Enetr The Institute Mail ID',Res:'danger'})
      setFormData({ email: '', password: '' ,username:''});
    }
    
  };
  return (
  <><Alert alert={alert}/>
    <div className='body'>
      
    <Routes>
      <Route path="/" element={<Navigate to="Login" />} />
      <Route path="Login" element={<UserLogin />} />
      <Route path="Register" element={<Register formData={formData} handleSubmit={handleSubmit} handleChange={handleChange} />} />
    </Routes></div></>
  );
}
