import React, { useState } from "react";
import IITILOGO from "./images/iitilogo.png";
import "./left.css";

export default function Left() {
 const [isExpanded, setIsExpanded] = useState(window.innerWidth > 768);

  useEffect(() => {
    const handleResize = () => {
      setIsExpanded(window.innerWidth > 768);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const hide = (e) => {
    e.preventDefault(); 
    setIsExpanded(!isExpanded);
  };

  return (
    <div className="left d-flex">
      <div className="firstleft d-flex">
        <a href="/" onClick={hide} className="top" aria-label="Toggle sidebar">
          <i className={`fa-solid fa-angles-${isExpanded ? "left" : "right"}`}></i>
        </a>
        <a href="/" className="mx-8" aria-label="QR Code">
          <i className="fa-solid fa-qrcode"></i>
        </a>
        <a href="/" aria-label="Profile">
          <i className="fa-regular fa-user"></i>
        </a>
        <a href="/" aria-label="TimeTable">
          <i className="fa-solid fa-table-cells"></i>
        </a>
        <a href="/" aria-label="Polls">
          <i className="fa-solid fa-square-poll-horizontal"></i>
        </a>
      </div>
      <div className={`secondleft d-flex ${isExpanded ? "" : "collapsed"}`}>
        <a href="/" onClick={hide} className="top" aria-label="Toggle sidebar">
          Hide
        </a>
        <a href="/">Dashboard</a>
        <a href="/">Profile</a>
        <a href="/">Enroll</a>
        <a href="/">TimeTable</a>
        <a href="/">Polls</a>
        <div className="logo">
          <img src={IITILOGO} alt="IITI Logo" />
        </div>
      </div>
    </div>
  );
}
