import React,{useState} from 'react'

export default function Request() {
  const [formData, setFormData] = useState({
    studentName: '',
    courseName: '',
    currentSlot: '',
    requestedSlot: '',
    reason: '',
  });
  const [status, setStatus] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = () => {
    if (!formData.studentName || !formData.courseName || !formData.currentSlot || !formData.requestedSlot || !formData.reason) {
      setStatus('Please fill out all fields.');
      return;
    }
    // Simulate sending request to admin (replace with API call)
    console.log('Submitting request:', formData);
    setStatus('Request submitted successfully!');
    setFormData({
      studentName: '',
      courseName: '',
      currentSlot: '',
      requestedSlot: '',
      reason: '',
    });
    setTimeout(() => setStatus(''), 3000);
  };

  const InputField = ({ label, name, value, onChange, placeholder }) => (
    <div>
      <label className="block text-sm font-medium text-gray-700">{label}</label>
      <input
        type="text"
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        className="mt-1 w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
  );

  const TextAreaField = ({ label, name, value, onChange, placeholder }) => (
    <div>
      <label className="block text-sm font-medium text-gray-700">{label}</label>
      <textarea
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        rows="4"
        className="mt-1 w-full p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 py-10">
      <div className="max-w-lg mx-auto bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
          Course Time/Slot Change Request
        </h2>
        {status && (
          <div className={`mb-4 p-2 text-center rounded ${status.includes('successfully') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {status}
          </div>
        )}
        <div className="space-y-4">
          <InputField
            label="Student Name"
            name="studentName"
            value={formData.studentName}
            onChange={handleInputChange}
            placeholder="Enter your full name"
          />
          <InputField
            label="Course Name"
            name="courseName"
            value={formData.courseName}
            onChange={handleInputChange}
            placeholder="Enter course name"
          />
          <InputField
            label="Current Time Slot"
            name="currentSlot"
            value={formData.currentSlot}
            onChange={handleInputChange}
            placeholder="e.g., Monday 10:00 AM - 11:00 AM"
          />
          <InputField
            label="Requested Time Slot"
            name="requestedSlot"
            value={formData.requestedSlot}
            onChange={handleInputChange}
            placeholder="e.g., Tuesday 2:00 PM - 3:00 PM"
          />
          <TextAreaField
            label="Reason for Change"
            name="reason"
            value={formData.reason}
            onChange={handleInputChange}
            placeholder="Explain why you need this change"
          />
          <button
            onClick={handleSubmit}
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition duration-200"
          >
            Submit Request
          </button>
        </div>
      </div>
    </div>
  );
}
