import React, { useState } from "react";
import "./Edit.css";

export default function Edit() {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    id: null,
    courseCode: "",
    name: "",
    day: "",
    startTime: "",
    endTime: "",
    professor: "",
    room: "",
    period: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const days = [
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "Sunday",
  ];
  const periods = ["Full Semester", "Half Semester", "Week"];
  const professors = [
    "Dr. Smith",
    "Dr. Jones",
    "Prof. Brown",
    "Dr. Taylor",
    "Prof. Wilson",
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !formData.courseCode ||
      !formData.name ||
      !formData.day ||
      !formData.startTime ||
      !formData.endTime ||
      !formData.professor ||
      !formData.room ||
      !formData.period
    ) {
      alert("Please fill all fields");
      return;
    }

    if (isEditing) {
      setCourses(
        courses.map((course) =>
          course.id === formData.id ? { ...formData } : course
        )
      );
      setIsEditing(false);
    } else {
      setCourses([
        ...courses,
        {
          ...formData,
          id: Date.now(),
        },
      ]);
    }

    setFormData({
      id: null,
      courseCode: "",
      name: "",
      day: "",
      startTime: "",
      endTime: "",
      professor: "",
      room: "",
      period: "",
    });
  };

  const handleEdit = (course) => {
    setFormData(course);
    setIsEditing(true);
  };

  const handleDelete = (id) => {
    setCourses(courses.filter((course) => course.id !== id));
  };

  const filteredCourses = courses.filter(
    (course) =>
      course.courseCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.professor.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <main
      style={{
        marginLeft: window.innerWidth < 768 ? "3rem" : "11.5rem",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "2rem",
      }}
    >
      <div className="container ">
        <h1 className="title">
          {isEditing ? "Edit Course Time Slot" : "Add Course Time Slot"}
        </h1>

        <div className="form-container">
          <div className="form-grid">
            <div>
              {" "}
              <label>Course Code</label>
              <input
                type="text"
                name="courseCode"
                value={formData.courseCode}
                onChange={handleInputChange}
                placeholder="Course Code"
                className="input"
              />
            </div>

            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Course Name"
              className="input"
            />
            <select
              name="day"
              value={formData.day}
              onChange={handleInputChange}
              className="input"
            >
              <option value="">Select Day</option>
              {days.map((day) => (
                <option key={day} value={day}>
                  {day}
                </option>
              ))}
            </select>
            <input
              type="time"
              name="startTime"
              value={formData.startTime}
              onChange={handleInputChange}
              className="input"
            />
            <input
              type="time"
              name="endTime"
              value={formData.endTime}
              onChange={handleInputChange}
              className="input"
            />
            <select
              name="professor"
              value={formData.professor}
              onChange={handleInputChange}
              className="input"
            >
              <option value="">
                Select Professor      </option>
                <option> <input
                  type="text"
                  value={searchTerm}
                  onChange={handleSearchChange}
                  placeholder="Search "
                  className="input search-input"
                /></option>
               
        
              {professors.map((prof) => (
                <option key={prof} value={prof}>
                  {prof}
                </option>
              ))}
            </select>
            <input
              type="text"
              name="room"
              value={formData.room}
              onChange={handleInputChange}
              placeholder="Room Number"
              className="input"
            />
            <select
              name="period"
              value={formData.period}
              onChange={handleInputChange}
              className="input"
            >
              <option value="">Select Period</option>
              {periods.map((period) => (
                <option key={period} value={period}>
                  {period}
                </option>
              ))}
            </select>
          </div>
          <button onClick={handleSubmit} className="submit-button">
            {isEditing ? "Update Course" : "Add Course"}
          </button>
        </div>

        <div className="search-container">
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search by Course Code, Name, or Professor"
            className="input search-input"
          />
        </div>

        <div className="table-container">
          <h2 className="table-title">Course Time Slots</h2>
          {filteredCourses.length === 0 ? (
            <p className="no-courses">No courses found.</p>
          ) : (
            <div className="table-wrapper">
              <table className="course-table">
                <thead>
                  <tr className="table-header">
                    <th className="table-cell">Course Code</th>
                    <th className="table-cell">Course Name</th>
                    <th className="table-cell">Day</th>
                    <th className="table-cell">Start Time</th>
                    <th className="table-cell">End Time</th>
                    <th className="table-cell">Professor</th>
                    <th className="table-cell">Room</th>
                    <th className="table-cell">Period</th>
                    <th className="table-cell">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredCourses.map((course) => (
                    <tr key={course.id} className="table-row">
                      <td className="table-cell">{course.courseCode}</td>
                      <td className="table-cell">{course.name}</td>
                      <td className="table-cell">{course.day}</td>
                      <td className="table-cell">{course.startTime}</td>
                      <td className="table-cell">{course.endTime}</td>
                      <td className="table-cell">{course.professor}</td>
                      <td className="table-cell">{course.room}</td>
                      <td className="table-cell">{course.period}</td>
                      <td className="table-cell">
                        <button
                          onClick={() => handleEdit(course)}
                          className="edit-button"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete(course.id)}
                          className="delete-button"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </main>
  );
}
