import React from "react";
import "./Enroll.css";

export default function Enroll(props) {
  const [formData, setFormData] = React.useState({
    fullname: "",
    email: "",
    courses: [], // Changed to array to handle multiple selections
  });

  const courseOptions = [
    { id: "maths-calculus-1", label: "Maths-calculus-1" },
    { id: "maths-differentiation-1", label: "Maths-Differentiation-1" },
    { id: "physics", label: "Physics" },
    { id: "chemistry", label: "Chemistry" },
    { id: "electronics", label: "Electronics" },
    { id: "manufacturing", label: "Manufacturing" },
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setFormData((prev) => ({
        ...prev,
        courses: checked
          ? [...prev.courses, value]
          : prev.courses.filter((course) => course !== value),
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.fullname && formData.email && formData.courses.length > 0) {
      console.log("Form submitted:", formData);
      setFormData({ fullname: "", email: "", courses: [] });
    }
  };

  return (
    <div className="m">
      <main
      className="enroll"
        style={{
          marginLeft: props.isExpanded
            ? window.innerWidth < 768
              ? "3rem"
              : "11.5rem"
            : window.innerWidth > 768
            ? "3rem"
            : "3rem",
        }}
      >
        <div className="min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8">
          <div className="bg-success p-6 sm:p-8 rounded-lg shadow-lg w-full max-w-xs sm:max-w-md lg:max-w-lg">
            <h1 className="text-xl sm:text-2xl lg:text-3xl font-bold mb-6 text-center">
              Course Enrollment
            </h1>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">
                  Full Name
                </label>
                <input
                  type="text"
                  name="fullname"
                  value={formData.fullname}
                  onChange={handleChange}
                  className="mt-1 block w-full p-2 sm:p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm sm:text-base"
                  placeholder="Enter your name"
                />
              </div>
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="mt-1 block w-full p-2 sm:p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm sm:text-base"
                  placeholder="Enter your email"
                />
              </div>
              <div>
                <label className="block text-sm sm:text-base font-medium text-gray-700">
                  Select Course
                </label>
                {courseOptions.map((course) => (
                  <div key={course.id} className="form-check">
                    <input
                      className="check"
                      type="checkbox"
                      name="courses"
                      value={course.id}
                      id={course.id}
                      checked={formData.courses.includes(course.id)}
                      onChange={handleChange}
                    />
                    <label className="form-check-label" htmlFor={course.id}>
                      {course.label}
                    </label>
                  </div>
                ))}
              </div>
              <button
                type="submit"
                className="w-full p-2 sm:p-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 text-sm sm:text-base"
              >
                Enroll Now
              </button>
            </form>
          </div>
        </div>
      </main>
    </div>
  );
}
