import React, { useState } from "react";
import { Routes, Route } from "react-router-dom";
import Userportal from "./Userportal";
import "./App.css";
import Header from "./components/Header";
import Left from "./components/Left";
import Right from "./components/Right";
import Dashboard from "./components/Dashboard";
import Profile from "./components/Profile";
import Enroll from "./components/Enroll";
import Timetable from "./components/Timetable";
import Polls from "./components/Polls";

function App() {
  const [isExpanded, setIsExpanded] = useState(true);

  const hide = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <>

     <Routes> 
      <Route path="/"  element={<Userportal/>}/>  
  <Route path="/Student" element={<><Header/><Dashboard/> <Left isExpanded={isExpanded} setIsExpanded={setIsExpanded} hide={hide} />  <Right /> </>}>
        <Route
          path="/Student/"
          element={
            <Dashboard isExpanded={isExpanded} setIsExpanded={setIsExpanded} />
          }
        />
        <Route
          path="/Student/Profile"
          element={
            <Profile isExpanded={isExpanded} setIsExpanded={setIsExpanded} />
          }
        />
        <Route
          path="/Student/Enroll"
          element={
            <Enroll isExpanded={isExpanded} setIsExpanded={setIsExpanded} />
          }
        />
        <Route
          path="/Student/Timetable"
          element={
            <Timetable isExpanded={isExpanded} setIsExpanded={setIsExpanded} />
          }
        />
        <Route
          path="/Student/Polls"
          element={
            <Polls isExpanded={isExpanded} setIsExpanded={setIsExpanded} />}
            />


   </Route>




      </Routes>
    </>
  );
}

export default App;
